import theme from '../components/theme'
import { useScrollPosition } from '../components/useScrollPosition'
import { useState, useRef, useEffect, forwardRef } from 'react'

const Index = () => {
    const el = useRef(null)
    const [headerStyle, setHeaderStyle] = useState({
        background: theme.COLORS.BG1
    })
    useScrollPosition(({ prevPos, currPos }) => {
        
        console.log(currPos.y)
        
    }, [headerStyle], el, false, 100)
    return (
        <div style={styles.container}>
            <header style={{...styles.header,  ...headerStyle}}>
                <h1 style={styles.text}>SELab</h1>
                <p style={styles.text}> Resarch</p>
                <p style={styles.text}> Courses</p>
            </header>
            <div style={styles.contentContainer1}>
                <p>1</p>
            </div>
            <div ref={el} style={styles.contentContainer2}>
                <p>2</p>
            </div>
            <div style={styles.contentContainer1}>
                <p>3</p>
            </div>
            <div style={styles.contentContainer2}>
                <p>4</p>
            </div>
            <style global jsx>{`
                body {
                    margin: 0;
                }
            `}</style>
        </div>
    )
}

const styles = {
    container: {
        ...theme.FONTS,
        margin: 0,
        padding: 0,

        // overflow: 'hidden',
    },
    header: {
        position: '-webkit-sticky',
        position: 'sticky',
        top: 0,
        height: 90,
        overflow: 'hidden',
        flex: 1,

    },
    text: {
        webkitBackgroundClip: 'text',
        webkitTextFillColor: 'transparent',
        whitesSpace: 'nowrap',
        display: 'inline-block',
        flex: 1,
        marginLeft: 10,
    },
    contentContainer1: {
        height: '50vh',
        color: 'white',
        backgroundColor: theme.COLORS.BG1,
        margin: 0,
        overflow: 'hidden',
    },
    contentContainer2: {
        height: '50vh',
        color: 'black',
        backgroundColor: theme.COLORS.BG2,
        margin: 0,
        overflow: 'hidden',

    }
}

export default Index
