import theme from '../components/theme'
import React, { useState, useRef, useEffect, forwardRef } from 'react'
import ScrollCollision from '../components/reactScrollCollision';


const App = () => (
    <div>
        <ScrollCollision className="Elem1">Scroll Down</ScrollCollision>
        <ScrollCollision className="Elem2"><a target="_blank" rel="noopener" href="https://github.com/Vince0382/react-scroll-collision">Click to see the project on GitHUB</a></ScrollCollision>
        <ScrollCollision className="Elem3">Scroll Down</ScrollCollision>
        <ScrollCollision className="ElemSVG">
            <svg width="100px" height="100px">
                <path d="M 30 40 L 70 40 C 90 40 90 75 60 85 A 40 40 0 0 1 20 20 L 80 80"></path>
                <path d="M 30 50 L 70 50"></path>
                <path d="M 70 60 L 30 60 C 10 60 10 20 40 15 A 40 38 0 1 1 20 80 L 80 20"></path>
            </svg>
        </ScrollCollision>
        <ScrollCollision className="Elem4"><a target="_blank" rel="noopener" href="https://www.npmjs.com/package/react-scroll-collision">Get the package from npm</a></ScrollCollision>
        <div className="Block-White">

        </div>
        <div className="Block-Black" data-clippath="ColorWhite">

        </div>
        <div className="Block-White">

        </div>
        <div className="Block-Black" data-clippath="ColorWhite">

        </div>

        <div className="Block-White">

        </div>
        <style global jsx>{`
            
            .Block-White,
            .Block-Black {
              width: 100%;
              height: 50vh;
            }
            
            .Block-White {
              background: ${theme.COLORS.BG2};
            }
            
            .Block-Black {
              background: ${theme.COLORS.BG1};
            }
            
            .ColorWhite{
              color: white;
              fill: white;
            }
            
            .ColorGray{
              color: lightgray;
              fill:gray;
            }
            
            .Elem1 {
              position: fixed;
              top: 25%;
              left: 10%;
              height: 20px;
              width: 300px;
            }
            
            .Elem2 {
              position: fixed;
              top: 5%;
              left: 50%;
              transform: translateX(-50%);
              height: 17px;
              width: 150px;
            }
            
            .Elem3 {
              position: fixed;
              top: 25%;
              right: 10%;
              height: 20px;
              width: 150px;
            }
            
            .ElemSVG {
              position: fixed;
              top: 30%;
              left: 50%;
              transform: translateX(-50%);
              height: 100px;
              width: 100px;
            }

            .ElemSVG svg{
                position: absolute;
                top: -33px;
                left: -33px;
            }
            
            .ElemSVG svg path{
                stroke: #fff;
                stroke-width: 6px;
                stroke-linecap: round;
                fill: transparent;
            }
                    `}
        </style>
    </div>
);


export default App
